mapper.insert:
INSERT INTO orderList (
    itemKey, userEmail, userName, userAddr, userAddrDetail, userNumber, optional, itemImg,
    itemSize, itemPrice, itemTotal, pitStatus, pitPrice, qty, orderStatus
) VALUES (
             #{itemKey}, #{userEmail}, #{userName}, #{userAddr}, #{userAddrDetail}, #{userNumber}, #{optional}, #{itemImg},
             #{itemSize}, #{itemPrice}, #{itemTotal}, #{pitStatus}, #{pitPrice}, #{qty}, #{orderStatus}
         )

mapper.update:
UPDATE orderList
SET orderStatus = #{orderStatus}
WHERE orderKey = #{orderKey}


UPDATE orderList
SET pitPrice = #{pitPrice}, orderStatus = #{orderStatus}
WHERE orderKey = #{orderKey}
